package com.jx.call.server_socket;public interface UsbListener {    /**     *连接成功     * @param msg     */    void onConnect(String msg);    /**     *断开连接     * @param msg     */    void onDisconnect(String msg);    /**     *链接错误     * @param msg     */    void onError(String msg);    /**     *下载数据/干警信息/轮次信息     * @param msg     */    void onDownLoad(String msg);    /**     *下载数据犯人数据     * @param msg     */    void onDownData(String msg);    /**     * 上传数据     * @param msg     */    void onUpLoad(String msg);}